mapper.update:
	UPDATE PROJECT
	
    	
     	 SET P_TITLE =#{title}
    	
    	
      	
      	SET P_REP_IMG = #{repImg}
      	
      	
      	
      	SET P_SUMMARY = #{summary}
      	
      	
      	
      	SET P_CATEGORY = #{category}
      	
      	
      	
      	SET P_PROFILE_IMG = #{profileImg}
      	
      	
      	
      	SET P_NAME = #{name}
      	
      	
      	
      	SET P_INTRODUCE = #{introduce}
      	
      	
      	
      	SET P_PRICE = #{price}
      	
      	
      	
      	SET P_END_DATE = #{endDate}
      	
      	
      	
      	SET P_REFUND = #{refund}
      	
      	
      	
      	
    	
	
	 WHERE EMAIL = #{email} AND P_PROJECT_NUMBER=#{projectNum}
